$(function () {
   $(document).scroll(function () {
     var $nav = $(".phoneVersion");
     $nav.toggleClass('scrolled', $(this).scrollTop() > $nav.height());

     var welcome = false;
     var info = false;
     var experience = false;
     var project = false;
     var leadership = false;
     var skills = false;
     var contact = false;

     if ($(this).scrollTop() >= $('#briefInfoSection').position().top && 
         $(this).scrollTop() < $('#ExperiencePage').position().top) {
       alert("briefInfo")
     }

   });

   $(".mainNavBar .nav-link").on("click", function(){
     // alert("motherfucker")
     $(".nav").find(".chosen").removeClass("chosen");
     $(this).addClass("chosen");
   });

 });
