import React , {useState, useEffect} from 'react';
import TopNav from '../TopNav';

import downArrow from '../../../assets/images/downArrow.png'

import { makeStyles } from '@material-ui/core/styles';


import "./mainHomeScreen.css";


// Kept here so that I can avoid fixing background URL
const useStyles = makeStyles(() => ({ boxWithArrow: {
        backgroundColor: '#5f5f5f',
        position: 'absolute',
        bottom: '0',
        right: '0',
        marginRight:'10px',
        width: '120px',
        height: '70px',
        borderTopRightRadius: '25px',
        borderTopLeftRadius: '25px',
        border: '1px solid white',
    },
    downArrowPic: {
        width: '35%',
        height: '35%',
        marginLeft: '35px',
        marginTop: '25px',
        animation: 'blink 2s infinite',
      }
}));

const MainHomeScreen = () => {
    const classes = useStyles();

	return (
		<section className="welcomeScreen">
            <TopNav/>
            <div className="welcomeMessage">Hello, I'm Aman</div>
            <a href="#briefInfoSection"><span class={classes.boxWithArrow}><img src={downArrow}
					className={classes.downArrowPic}/></span></a>
        </section>
	);
};

export default MainHomeScreen;
